import Link from "next/link"
import Image from "next/image"
import { Search } from "lucide-react"
import { Input } from "@/components/ui/input"
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card"

const categories = [
  { name: "Vegetables", image: "https://th.bing.com/th/id/OIP.GAxu4tAIZrcEWJGnktND-wAAAA?w=281&h=180&c=7&r=0&o=7&dpr=1.3&pid=1.7&rm=3", hint: "fresh vegetables" },
  { name: "Spices & Masalas", image: "https://res.cloudinary.com/enchanting/q_90,f_auto,c_lfill,w_400,h_300/enchanting-web/2023/08/various-spices-paprika-turmeric-pepper-aniseed-cinnamon-saffron-shutterstock_144788905.jpg", hint: "assorted spices" },
  { name: "Grains & Flour", image: "https://tse4.mm.bing.net/th/id/OIP.jHeWy-QLSWrrBMr-1PMSugHaE-?rs=1&pid=ImgDetMain&o=7&rm=3", hint: "sacks of grain" },
  { name: "Oils & Ghee", image: "https://th.bing.com/th/id/OIP.6-D2NfaU3GAR8q-J-EXG1wHaFj?w=238&h=180&c=7&r=0&o=7&dpr=1.3&pid=1.7&rm=3", hint: "cooking oil bottles" },
  { name: "Dairy & Paneer", image: "data:image/webp;base64,", hint: "cheese blocks" },
  { name: "Snacks & Pellets", image: "https://th.bing.com/th/id/OIP.CNfKlQ2nuYdfLb48m5zegAHaE8?w=236&h=180&c=7&r=0&o=7&dpr=1.3&pid=1.7&rm=3", hint: "puri pellets" },
  { name: "Chutneys & Sauces", image: "https://th.bing.com/th/id/OIP.C0P5UdVZpECmwEeRiJPeogHaHa?w=192&h=192&c=7&r=0&o=7&dpr=1.3&pid=1.7&rm=3", hint: "chutney bowls" },
  { name: "Disposables", image: "https://t3.ftcdn.net/jpg/00/77/06/84/360_F_77068473_Y7mV2jx7EnlWELlYEfQOj4zB9jt5FJxe.jpg", hint: "paper plates cups" },
];

export default function VendorDashboardPage() {
  return (
    <div className="flex flex-col gap-8">
      <div>
        <h1 className="text-3xl font-bold tracking-tight">Find Ingredients for Your Stall</h1>
        <p className="text-muted-foreground">Search for ingredients and compare suppliers in your city.</p>
      </div>
      <div className="relative">
        <Search className="absolute left-4 top-1/2 -translate-y-1/2 h-5 w-5 text-muted-foreground" />
        <Input
          type="search"
          placeholder="Search for items like 'potatoes', 'chaat masala', 'pani puri'..."
          className="w-full rounded-full bg-card pl-12 h-14 text-base"
        />
      </div>

      <Card>
        <CardHeader>
            <CardTitle>Browse by Category</CardTitle>
            <CardDescription>Explore ingredients from our wide range of categories.</CardDescription>
        </CardHeader>
        <CardContent>
            <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-6">
                {categories.map(category => (
                    <Link href="/vendor/compare" key={category.name}>
                        <div className="group relative overflow-hidden rounded-lg shadow-lg hover:shadow-primary/50 transition-shadow duration-300">
                            <Image
                                src={category.image}
                                alt={category.name}
                                data-ai-hint={category.hint}
                                width={400}
                                height={300}
                                className="object-cover w-full h-48 transition-transform duration-300 ease-in-out group-hover:scale-110"
                            />
                            <div className="absolute inset-0 bg-gradient-to-t from-black/80 to-transparent flex items-end p-4">
                                <h3 className="text-lg font-semibold text-white">{category.name}</h3>
                            </div>
                        </div>
                    </Link>
                ))}
            </div>
        </CardContent>
      </Card>
    </div>
  )
}
