import Link from "next/link"
import Image from "next/image"
import { Search } from "lucide-react"
import { Input } from "@/components/ui/input"
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card"

const categories = [
  { name: "Vegetables", image: "https://www.alamy.com/stock-photo-fresh-vegetables-on-a-market-stall-53206343.html?imageid=8598779F-9A13-4841-B30B-3E2B7E796DB2&p=16392&pn=1&searchId=a3bf05462119743471db2b7737f84595&searchtype=0", hint: "fresh vegetables" },
  { name: "Spices & Masalas", image: "https://www.alamy.com/stock-photo-indian-curry-masala-curry-masalaspicesindian-spices-53777102.html?imageid=0A0EE52A-0561-4175-BC87-550137485042&p=176201&pn=1&searchId=bc82d9be2e38efaf29b2a6594b56d9c2&searchtype=0 ", hint: "assorted spices" },
  { name: "Grains & Flour", image: "https://www.alamy.com/stock-photo-ear-grains-flour-and-wholegrains-on-wood-table-91190161.html?imageid=D08DB61E-4F61-4F10-9472-825D7FAB708E&p=280319&pn=1&searchId=6104ec38d442c9f80a6f8bb6ea8d939b&searchtype=0", hint: "sacks of grain" },
  { name: "Oils & Ghee", image: "https://www.alamy.com/ghee-or-clarified-butter-in-jar-healthy-eating-and-using-organic-fresh-made-products-healthy-ingredient-for-cooking-organic-meal-image351586947.html?imageid=D3D3B4EF-4060-47D5-A834-C1135E725847&p=1222850&pn=1&searchId=8193c8f22c3bd60eb890aa1cd7224ffe&searchtype=0", hint: "cooking oil bottles" },
  { name: "Dairy & Paneer", image: "data:image/webp;base64,", hint: "cheese blocks" },
  { name: "Snacks & Pellets", image: "https://www.alamy.com/stock-photo-a-tray-of-cheese-and-fruit-snacks-consisting-of-cheddar-cheese-crackers-31500511.html?imageid=7EF10A85-E93B-4584-91EE-429BB445BD89&p=40651&pn=1&searchId=6d93ed73be9898ef28ce6e76bd976a7c&searchtype=0", hint: "puri pellets" },
  { name: "Chutneys & Sauces", image: "https://www.alamy.com/different-tasty-sauces-in-bowls-on-white-table-flat-lay-restaurant-style-vegetable-raita-and-green-chutney-spicy-yogurt-mint-chutney-pudina-raita-image617755907.html?imageid=FA2D1BAA-F536-464F-A71F-A4C1DB10B3F8&p=2346523&pn=1&searchId=ae7ae6c2f27089ce71bc25f1417f97b3&searchtype=0", hint: "chutney bowls" },
  { name: "Disposables", image: "https://www.bing.com/images/search?view=detailV2&ccid=GhXo7Zgz&id=2F0A15526F8F71E14A123A461A8D01AD19E52E07&thid=OIP.GhXo7ZgzrflnTRbZFyW5FQHaE8&mediaurl=https%3a%2f%2faradbranding.com%2fen%2fwp-content%2fuploads%2f2022%2f08%2f2958194_5f3ce13830229-1.jpg&exph=800&expw=1200&q=disposables&simid=608048292311075025&FORM=IRPRST&ck=3F8B8712DEA0CC832825D226891E1300&selectedIndex=3&itb=0", hint: "paper plates cups" },
];

export default function VendorDashboardPage() {
  return (
    <div className="flex flex-col gap-8">
      <div>
        <h1 className="text-3xl font-bold tracking-tight">Find Ingredients for Your Stall</h1>
        <p className="text-muted-foreground">Search for ingredients and compare suppliers in your city.</p>
      </div>
      <div className="relative">
        <Search className="absolute left-4 top-1/2 -translate-y-1/2 h-5 w-5 text-muted-foreground" />
        <Input
          type="search"
          placeholder="Search for items like 'potatoes', 'chaat masala', 'pani puri'..."
          className="w-full rounded-full bg-card pl-12 h-14 text-base"
        />
      </div>

      <Card>
        <CardHeader>
            <CardTitle>Browse by Category</CardTitle>
            <CardDescription>Explore ingredients from our wide range of categories.</CardDescription>
        </CardHeader>
        <CardContent>
            <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-6">
                {categories.map(category => (
                    <Link href="/vendor/compare" key={category.name}>
                        <div className="group relative overflow-hidden rounded-lg shadow-lg hover:shadow-primary/50 transition-shadow duration-300">
                            <Image
                                src={category.image}
                                alt={category.name}
                                data-ai-hint={category.hint}
                                width={400}
                                height={300}
                                className="object-cover w-full h-48 transition-transform duration-300 ease-in-out group-hover:scale-110"
                            />
                            <div className="absolute inset-0 bg-gradient-to-t from-black/80 to-transparent flex items-end p-4">
                                <h3 className="text-lg font-semibold text-white">{category.name}</h3>
                            </div>
                        </div>
                    </Link>
                ))}
            </div>
        </CardContent>
      </Card>
    </div>
  )
}
